package data

import (
	"github.com/apache/arrow/go/arrow/array"
)

// Series implemented on the top of Apache Arrow.
// Most of the code is autogenerated and lives in arrow.gen.go

func mustNewArrowSeriesFromSlice(col ColumnName, values interface{}, notNull []bool) *Series {
	series, err := newArrowSeriesFromSlice(col, values, notNull)
	handle(err)
	return series
}

// common metadata for all Arrow series

type arrowSeriesMeta struct {
	values array.Interface
}

func (m *arrowSeriesMeta) IsMaterialized() bool { return true }

func (m *arrowSeriesMeta) ExactSize() int {
	return m.values.Len()
}
func (m *arrowSeriesMeta) MaxSize() int {
	return m.values.Len()
}

var _ boundedMeta = (*arrowSeriesMeta)(nil)
